CREATE TABLE IF NOT EXISTS users (id SERIAL PRIMARY KEY, first_name VARCHAR(255) NOT NULL, last_name VARCHAR(255) NOT NULL, user_name VARCHAR(255) NOT NULL, password VARCHAR(255) NOT NULL, is_active BOOLEAN NOT NULL, prefix INTEGER);
CREATE TABLE IF NOT EXISTS training_types (id SERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL);
CREATE TABLE IF NOT EXISTS trainees (id BIGINT PRIMARY KEY, date_of_birth DATE, address VARCHAR(255), FOREIGN KEY (id) REFERENCES users(id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS trainers (id BIGINT PRIMARY KEY, specialization BIGINT NOT NULL, FOREIGN KEY (specialization) REFERENCES training_types (id) ON DELETE CASCADE, FOREIGN KEY (id) REFERENCES users(id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS trainings (id SERIAL PRIMARY KEY, trainee_id BIGINT NOT NULL, trainer_id BIGINT NOT NULL, name VARCHAR(255) NOT NULL, type_id BIGINT NOT NULL, date DATE NOT NULL, duration BIGINT NOT NULL, FOREIGN KEY (trainee_id) REFERENCES trainees(id), FOREIGN KEY (trainer_id) REFERENCES trainers(id), FOREIGN KEY (type_id) REFERENCES training_types(id));
CREATE TABLE IF NOT EXISTS trainee_trainer (trainee_id BIGINT NOT NULL, trainer_id BIGINT NOT NULL, PRIMARY KEY (trainee_id, trainer_id), CONSTRAINT fk_trainee FOREIGN KEY (trainee_id) REFERENCES trainees(id), CONSTRAINT fk_trainer FOREIGN KEY (trainer_id) REFERENCES trainers(id));
